import React, { useState, useEffect } from 'react';
import '../../App.css';

const RegistrationForm = ({ title, fields, onSubmit, isSubmitting }) => {
  const [formData, setFormData] = useState({});
  const [syncPercent, setSyncPercent] = useState(0);
  const [isInternalSyncing, setIsInternalSyncing] = useState(false);

  useEffect(() => {
    const initialData = {};
    fields.forEach(field => {
      if (field.value !== undefined) {
        initialData[field.id] = field.value;
      }
    });
    setFormData(initialData);
  }, [fields]);

  const handleChange = (e) => {
    const { name, value, files, type } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === 'file' ? files[0] : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsInternalSyncing(true);

    let progress = 0;
    const interval = setInterval(() => {
      progress += Math.floor(Math.random() * 15) + 5;
      if (progress >= 100) {
        setSyncPercent(100);
        clearInterval(interval);
        setTimeout(() => {
          if (onSubmit) onSubmit(formData);
          setIsInternalSyncing(false);
          setSyncPercent(0);
        }, 800);
      } else {
        setSyncPercent(progress);
      }
    }, 100);
  };

  return (
    <div className="w-full relative py-10">
      <div className="bg-[#0a1a14]/90 backdrop-blur-3xl border border-[#39ff14]/20 rounded-3xl p-6 sm:p-12 shadow-2xl">
        <div className="text-center mb-12">
          <h2 className="font-orbitron text-xl sm:text-3xl uppercase text-[#39ff14] tracking-widest">
            {title}
          </h2>
          <div className="h-[2px] w-16 bg-[#00ffd5] mx-auto mt-4 opacity-30"></div>
        </div>

        <form onSubmit={handleSubmit} className="space-y-8">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-x-10 gap-y-8">
            {fields.map((field) => (
              <div key={field.id} className={`${field.fullWidth ? "md:col-span-2" : ""}`}>
                <label className="block font-orbitron text-[10px] uppercase tracking-widest text-[#00ffd5] mb-3">
                  {field.label} {field.required && <span className="text-[#39ff14] ml-1">*</span>}
                </label>

                {field.type === 'select' ? (
                  <select
                    name={field.id}
                    value={formData[field.id] || ""}
                    required={field.required}
                    onChange={handleChange}
                    className="w-full bg-black/40 border border-white/10 rounded-xl px-5 py-4 text-white font-roboto appearance-none focus:border-[#39ff14]/50 outline-none transition-all"
                  >
                    <option value="" disabled className="bg-[#030f0a]">SELECT_OPTION</option>
                    {field.options?.map((opt) => (
                      <option key={opt} value={opt} className="bg-[#030f0a]">{opt}</option>
                    ))}
                  </select>
                ) : (
                  <input
                    type={field.type || "text"}
                    name={field.id}
                    value={formData[field.id] || ""}
                    placeholder={field.placeholder}
                    required={field.required}
                    onChange={handleChange}
                    className="w-full bg-black/40 border border-white/10 rounded-xl px-5 py-4 text-white placeholder:text-white/20 focus:border-[#39ff14]/50 outline-none transition-all"
                  />
                )}
              </div>
            ))}
          </div>

          <div className="pt-10">
            <button
              type="submit"
              disabled={isInternalSyncing || isSubmitting}
              className="w-full h-16 bg-gradient-to-r from-[#39ff14] to-[#00ffd5] rounded-xl font-orbitron font-black text-black text-sm sm:text-lg tracking-widest uppercase hover:scale-[1.02] active:scale-95 transition-all shadow-lg disabled:opacity-50"
            >
              {isInternalSyncing ? `SYNCING_${syncPercent}%` : 'INITIALIZE_REGISTRATION'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default RegistrationForm;
