import React, { useState, useEffect } from 'react';
import '../../App.css';

const RegistrationForm = ({ title, fields, onFieldUpdate, onSubmit, isSubmitting }) => {
  const [formData, setFormData] = useState({});
  const [syncPercent, setSyncPercent] = useState(0);
  const [isInternalSyncing, setIsInternalSyncing] = useState(false);
  const [dynamicFields, setDynamicFields] = useState(fields);

  useEffect(() => {
    setDynamicFields(fields);
    const initialData = {};
    fields.forEach(field => {
      if (field.value !== undefined) {
        initialData[field.id] = field.value;
      }
    });
    setFormData(prev => ({ ...prev, ...initialData }));
  }, [fields]);

  const handleChange = (e) => {
    const { name, value, files, type } = e.target;
    const fieldValue = type === 'file' ? files[0] : value;
    setFormData((prev) => ({ ...prev, [name]: fieldValue }));

    if (onFieldUpdate) {
      onFieldUpdate(name, fieldValue);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setIsInternalSyncing(true);

    let progress = 0;
    const interval = setInterval(() => {
      progress += Math.floor(Math.random() * 15) + 5;
      if (progress >= 100) {
        setSyncPercent(100);
        clearInterval(interval);
        setTimeout(() => {
          if (onSubmit) onSubmit(formData);
          setIsInternalSyncing(false);
          setSyncPercent(0);
        }, 800);
      } else {
        setSyncPercent(progress);
      }
    }, 150);
  };

  return (
    <div className="w-full max-w-4xl mx-auto relative group px-2 sm:px-6 lg:px-8 my-6 md:my-10">
      <div className="bg-black/85 backdrop-blur-3xl border border-white/10 rounded-2xl p-6 sm:p-10 md:p-16 shadow-[0_0_60px_rgba(0,0,0,0.9)] relative">
        <div className="text-center mb-8 md:mb-16">
          <h2 className="font-orbitron text-xl sm:text-3xl md:text-5xl uppercase text-gradient-ai glow-ai mb-4 leading-tight">
            {title}
          </h2>
          <div className="h-[1px] w-20 bg-[#39ff14] mx-auto opacity-50"></div>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6 md:space-y-12">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-x-8 gap-y-6 md:gap-y-10">
            {dynamicFields.map((field) => (
              <div key={field.id} className={`${field.fullWidth ? "md:col-span-2" : ""}`}>
                <label className="block font-orbitron text-[10px] sm:text-[11px] uppercase tracking-[0.15em] text-[#00ffd5] mb-2 sm:mb-3">
                  {field.label} {field.required && <span className="text-red-500">*</span>}
                </label>
                <div className="relative">
                  {field.type === 'select' ? (
                    <select
                      name={field.id}
                      value={formData[field.id] || ""}
                      required={field.required}
                      onChange={handleChange}
                      className="w-full bg-white/5 border border-white/10 rounded-lg px-4 py-3 sm:py-4 text-white font-roboto appearance-none focus:border-[#39ff14]/50 outline-none transition-colors"
                    >
                      <option value="" className="bg-[#030f0a]" disabled>SELECT_OPTION</option>
                      {field.options?.map((opt) => (
                        <option key={opt} value={opt} className="bg-[#030f0a] text-white">
                          {opt}
                        </option>
                      ))}
                    </select>
                  ) : field.type === 'file' ? (
                    <div className="relative">
                      <input
                        type="file"
                        name={field.id}
                        required={field.required}
                        onChange={handleChange}
                        accept="image/*"
                        className="w-full bg-white/5 border border-white/10 rounded-lg px-4 py-3 sm:py-4 text-white file:bg-[#39ff14] file:text-black file:border-none file:rounded-md file:px-3 file:py-1 file:mr-4 file:text-xs file:font-bold file:uppercase cursor-pointer"
                      />
                    </div>
                  ) : (
                    <input
                      type={field.type || "text"}
                      name={field.id}
                      value={formData[field.id] || ""}
                      placeholder={field.placeholder}
                      required={field.required}
                      onChange={handleChange}
                      className="w-full bg-white/5 border border-white/10 rounded-lg px-4 py-3 sm:py-4 text-white placeholder:text-white/20 focus:border-[#39ff14]/50 outline-none transition-all"
                    />
                  )}
                </div>
              </div>
            ))}
          </div>

          <div className="pt-6 md:pt-12">
            <button
              type="submit"
              disabled={isInternalSyncing || isSubmitting}
              className="w-full h-14 sm:h-20 bg-gradient-to-r from-[#39ff14] via-[#00ffd5] to-[#39ff14] rounded-xl font-orbitron font-black text-black text-sm sm:text-lg tracking-widest uppercase hover:scale-[1.01] active:scale-95 transition-all shadow-[0_0_20px_rgba(57,255,20,0.3)] disabled:opacity-50"
            >
              {isInternalSyncing ? `SYNCING_${syncPercent}%` : 'INITIALIZE_REGISTRATION'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default RegistrationForm;
