  const currentEvent = EVENTS[selectedEvent];
  const hasEntryFee = currentEvent.fee && Number(currentEvent.fee.toString().replace(/[^0-9.-]+/g, "")) > 0;

  const handleSelect = (key) => {
    navigate(`/register/${key}`);
    setIsDropdownOpen(false);
  };

  const getDynamicFields = () => {
    let baseFields = [...currentEvent.fields];

    if (currentEvent.isTeamEvent && currentEvent.teamConfig) {
      const { min, max } = currentEvent.teamConfig;
      const options = Array.from({ length: max - min + 1 }, (_, i) => (min + i).toString());
      const insertionIndex = baseFields.findIndex(f => f.id === 'paymentScreenshot');

      const memberCountSelector = {
        id: 'dynamicMemberCount',
        label: 'Total Team Members',
        type: 'select',
        options: options,
        value: memberCount.toString(),
        onChange: (e) => setMemberCount(parseInt(e.target.value, 10)),
        required: true,
        fullWidth: true
      };

      const memberFields = [];
      const labelPrefix = selectedEvent === 'gaming' ? 'Player' : 'Member';

      for (let i = 1; i <= memberCount; i++) {
        const isLeader = (i === 1 && (baseFields.some(f => f.id === 'leaderName' || f.id === 'fullName')));
        if (isLeader) continue;

        memberFields.push({
          id: `player${i}Name`,
          label: `${labelPrefix} ${i} Name`,
          placeholder: `Name of ${labelPrefix.toLowerCase()} ${i}`,
          required: true,
          fullWidth: true
        });

        if (selectedEvent === 'gaming') {
          memberFields.push({
            id: `player${i}GameId`,
            label: `${labelPrefix} ${i} ID`,
            placeholder: `Game ID for player ${i}`,
            required: true
          });
        }
      }
      baseFields.splice(insertionIndex, 0, memberCountSelector, ...memberFields);
    }
    return baseFields;
  };

  const handleMasterSubmit = async (formData) => {
    if (isSubmitting) return;
    setIsSubmitting(true);
    setTimeout(() => {
        setIsSubmitting(false);
        setIsRegistered(true);
    }, 1500);
  };

  return (
    /* FIXED: Changed h-screen to min-h-screen and removed overflow-hidden */
    <div className="text-white min-h-screen w-full relative bg-[#030f0a] flex flex-col">
      <button
        onClick={() => navigate('/events')}
        className="fixed top-4 right-4 sm:top-6 sm:right-6 z-[250] font-orbitron text-[9px] sm:text-[10px] uppercase tracking-widest text-[#39ff14] border border-[#39ff14]/50 px-3 py-2 sm:px-4 sm:py-2 rounded-full bg-black/80 backdrop-blur-md hover:bg-[#39ff14] hover:text-black transition-all cursor-pointer"
      >
        ✕ Exit
      </button>

      <SidebarNav activeNav={activeNav} setActiveNav={setActiveNav} />
      <SocialSidebar />
      <Footer activeNav={activeNav} setActiveNav={setActiveNav} />

      {/* FIXED: Ensure this container allows scrolling */}
      <div className="flex-1 w-full h-full relative px-4 sm:px-8 md:pl-28 md:pr-24 no-scrollbar flex flex-col">

        {/* Responsive Background */}
        <div className="fixed inset-0 z-0 pointer-events-none">
          {currentEvent.bgImage && (
            <img
              key={currentEvent.bgImage}
              src={currentEvent.bgImage}
              alt="BG"
              className="w-full h-full object-cover opacity-30 transition-opacity duration-1000"
            />
          )}
          <div className="absolute inset-0 bg-gradient-to-b from-black via-black/40 to-black"></div>
        </div>

        {/* Dropdown Selector */}
        <div className="z-[100] mt-16 sm:mt-20 flex flex-col items-center relative w-full max-w-[300px] mx-auto">
          <button
            onClick={() => setIsDropdownOpen(!isDropdownOpen)}
            className="group relative w-full bg-black/80 backdrop-blur-2xl border border-[#39ff14]/30 p-3 sm:p-4 rounded-md flex items-center justify-between transition-all"
          >
            <div className="flex flex-col items-start text-left">
              <span className="text-[6px] sm:text-[7px] text-[#00ffd5] font-orbitron uppercase tracking-widest opacity-50 mb-1">Status: Active</span>
              <span className="text-[#39ff14] font-orbitron text-[10px] sm:text-[11px] uppercase tracking-wider truncate max-w-[180px]">{currentEvent.name}</span>
            </div>
            <svg className={`w-4 h-4 text-[#39ff14] transition-transform ${isDropdownOpen ? 'rotate-180' : ''}`} fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
            </svg>
          </button>

          {isDropdownOpen && (
            <div className="absolute top-full left-0 w-full mt-2 bg-[#030f0a]/95 backdrop-blur-3xl border border-white/10 rounded-md overflow-hidden z-[110] shadow-2xl">
              <div className="max-h-[250px] overflow-y-auto py-1 no-scrollbar">
                {Object.keys(EVENTS).map((key) => (
                  <button
                    key={key}
                    onClick={() => handleSelect(key)}
                    className={`w-full text-left px-4 py-3 font-orbitron text-[9px] sm:text-[10px] uppercase tracking-widest transition-all
                      ${selectedEvent === key ? 'bg-[#39ff14]/10 text-[#39ff14]' : 'text-gray-400 hover:bg-white/5 hover:text-white'}`}
                  >
                    {EVENTS[key].name}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>

        {/* FIXED: Main content area needs to push down and allow scroll */}
        <main className="z-10 py-8 sm:py-10 flex flex-col items-center w-full min-h-max pb-32">
          {!isRegistered ? (
             <div className="w-full flex flex-col items-center">
                <div className="max-w-3xl w-full mb-8 sm:mb-10 text-center px-2">
                    <h1 className="font-orbitron text-2xl sm:text-4xl md:text-6xl font-bold text-[#39ff14] uppercase tracking-widest drop-shadow-[0_0_15px_rgba(57,255,20,0.4)] leading-tight">
                      {currentEvent.name}
                    </h1>
                    <div className="flex justify-center flex-wrap gap-2 sm:gap-4 mt-4 font-orbitron text-[8px] sm:text-[10px] text-[#00ffd5] opacity-80 uppercase tracking-widest">
                      <span>VENUE: {currentEvent.venue}</span>
                      <span className="hidden sm:inline opacity-30">|</span>
                      <span>DATE: {currentEvent.date}</span>
                    </div>

                    <div className="mt-8 bg-black/40 backdrop-blur-md border border-white/5 p-5 sm:p-8 rounded-xl text-left">
                      <p className="text-gray-300 font-light text-sm sm:text-base leading-relaxed mb-6">{currentEvent.description}</p>

                      <div className={`grid grid-cols-1 ${hasEntryFee ? 'lg:grid-cols-2' : ''} gap-8 border-t border-white/10 pt-6`}>
                        <div className="space-y-3 text-xs sm:text-sm uppercase tracking-wider text-[#39ff14]/90">
                          {currentEvent.details.map((detail, index) => (
                            <p key={index} className="leading-relaxed">{detail}</p>
                          ))}
                        </div>

                        {hasEntryFee && (
                        <div className="bg-[#39ff14]/5 p-6 rounded-xl border border-[#39ff14]/20 flex flex-col items-center text-center">
                            <p className="text-[#39ff14] text-[10px] sm:text-[12px] font-orbitron mb-4 tracking-widest uppercase">Payment Gateway</p>
                            <img src="/qrcode.png" alt="QR" className="w-48 h-48 sm:w-56 sm:h-56 object-cover rounded-lg mb-4 bg-white p-2" />
                            <a href="/qrcode.png" download className="text-[#39ff14] border border-[#39ff14]/30 px-5 py-2 rounded-full text-[9px] font-orbitron uppercase tracking-tighter hover:bg-[#39ff14] hover:text-black transition-all">Save QR Code</a>
                        </div>
                        )}
                      </div>
                    </div>
                </div>

                <div className="w-full">
                    <RegistrationForm
                      key={`${selectedEvent}-${memberCount}`}
                      title={`${currentEvent.name} Terminal`}
                      fields={getDynamicFields()}
                      onSubmit={handleMasterSubmit}
                      isSubmitting={isSubmitting}
                    />
                </div>
             </div>
          ) : (
            <div className="max-w-2xl w-full bg-black/80 backdrop-blur-3xl border-2 border-[#39ff14]/30 p-8 sm:p-12 rounded-2xl text-center shadow-[0_0_50px_rgba(57,255,20,0.1)] animate-in fade-in zoom-in duration-500 my-10 sm:my-20 mx-4">
                <div className="w-16 h-16 sm:w-24 sm:h-24 border-2 border-[#39ff14] rounded-full flex items-center justify-center mx-auto mb-6 sm:mb-8 shadow-[0_0_20px_#39ff14]">
                    <svg className="w-8 h-8 sm:w-12 sm:h-12 text-[#39ff14]" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="3" d="M5 13l4 4L19 7" />
                    </svg>
                </div>
                <h2 className="font-orbitron text-xl sm:text-3xl text-[#39ff14] tracking-widest uppercase mb-4 sm:mb-6 leading-tight">
                    Transmission Success
                </h2>
                <div className="h-[2px] w-12 sm:w-20 bg-[#00ffd5] mx-auto mb-6 sm:mb-8"></div>
                <p className="font-roboto text-gray-300 text-sm sm:text-lg leading-relaxed mb-8 sm:mb-10 px-2">
                    Neural link established. You are now registered for <span className="text-[#39ff14] font-bold">{currentEvent.name}</span>.
                    Join the official sector hub on WhatsApp for briefing updates.
                </p>

                <a
                    href={currentEvent.whatsappLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="w-full sm:w-auto inline-flex items-center justify-center gap-3 bg-[#25D366] text-white px-8 py-4 sm:px-10 sm:py-5 rounded-full font-orbitron text-sm sm:text-lg uppercase tracking-wider hover:brightness-110 transition-all transform hover:scale-105 active:scale-95 shadow-[0_10px_30px_rgba(37,211,102,0.3)]"
                >
                    Join WhatsApp Hub
                </a>

                <button
                    onClick={() => setIsRegistered(false)}
                    className="block mx-auto mt-8 sm:mt-10 font-orbitron text-[9px] sm:text-[10px] text-[#00ffd5]/60 hover:text-[#39ff14] transition-colors uppercase tracking-[0.2em]"
                >
                    ← Back to Terminal
                </button>
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default EventMasterTerminal;
