import React, { useState, useEffect } from 'react';
import '../../App.css';

const RegistrationForm = ({ title, fields, onSubmit, isSubmitting }) => {
  const [formData, setFormData] = useState({});
  const [syncPercent, setSyncPercent] = useState(0);
  const [isProcessing, setIsProcessing] = useState(false); //

  useEffect(() => {
    const initialData = {};
    fields.forEach(field => {
      if (field.value !== undefined) initialData[field.id] = field.value;
    });
    setFormData(initialData);
  }, [fields]);

  const handleChange = (e) => {
    const { name, value, files, type } = e.target;
    setFormData(prev => ({ ...prev, [name]: type === 'file' ? files[0] : value }));
  };

  const handleInternalSubmit = (e) => {
    e.preventDefault();
    setIsProcessing(true); //

    let progress = 0;
    const interval = setInterval(() => {
      progress += Math.floor(Math.random() * 15) + 5;
      if (progress >= 100) {
        setSyncPercent(100);
        clearInterval(interval);
        setTimeout(() => {
          if (onSubmit) onSubmit(formData); // Parent handleMasterSubmit called here
          setIsProcessing(false);
        }, 800);
      } else {
        setSyncPercent(progress);
      }
    }, 150);
  };

  return (
    <div className="w-full relative group">
      {/* Visual Glitch Decorators */}
      <div className="absolute -top-2 -left-2 w-8 h-8 border-t-2 border-l-2 border-[#39ff14]"></div>
      <div className="absolute -bottom-2 -right-2 w-8 h-8 border-b-2 border-r-2 border-[#00ffd5]"></div>

      <div className="bg-black/80 backdrop-blur-2xl border border-white/10 rounded-xl p-8 md:p-12 relative overflow-hidden">
        <h3 className="font-orbitron text-2xl text-[#39ff14] mb-8 uppercase tracking-widest text-center">{title}</h3>

        <form onSubmit={handleInternalSubmit} className="space-y-8">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {fields.map((field) => (
              <div key={field.id} className={field.fullWidth ? "md:col-span-2" : ""}>
                <label className="block font-orbitron text-[10px] text-[#00ffd5] uppercase mb-2 tracking-widest">
                  {field.label} {field.required && <span className="text-[#ff0044] animate-pulse">*</span>}
                </label>

                {field.type === 'select' ? (
                  <select
                    name={field.id}
                    required={field.required}
                    value={formData[field.id] || ""}
                    onChange={handleChange}
                    className="w-full bg-white/5 border border-white/20 rounded-lg p-4 text-white font-roboto outline-none focus:border-[#39ff14]"
                  >
                    <option value="" disabled className="bg-black">SELECT_OPTION</option>
                    {field.options?.map(opt => <option key={opt} value={opt} className="bg-black">{opt}</option>)}
                  </select>
                ) : (
                  <input
                    type={field.type || "text"}
                    name={field.id}
                    placeholder={field.placeholder}
                    required={field.required}
                    onChange={handleChange}
                    className="w-full bg-white/5 border border-white/20 rounded-lg p-4 text-white font-roboto outline-none focus:border-[#39ff14]"
                  />
                )}
              </div>
            ))}
          </div>

          <button
            type="submit"
            disabled={isProcessing || isSubmitting}
            className="w-full relative h-16 rounded-lg bg-gradient-to-r from-[#39ff14] to-[#00ffd5] text-black font-orbitron font-black uppercase tracking-widest overflow-hidden transition-all hover:shadow-[0_0_30px_rgba(57,255,20,0.5)]"
          >
            <span className="relative z-10">{isProcessing ? `SYNCING_${syncPercent}%` : 'INITIALIZE_PROTOCOL'}</span>
            {isProcessing && (
              <div className="absolute top-0 left-0 h-full bg-white/30 transition-all duration-300" style={{ width: `${syncPercent}%` }}></div>
            )}
          </button>
        </form>
      </div>
    </div>
  );
};

export default RegistrationForm;
