    navigate(`/register/${key}`);
    setIsDropdownOpen(false);
  };

  const getDynamicFields = () => {
    let baseFields = [...currentEvent.fields];
    if (currentEvent.isTeamEvent && currentEvent.teamConfig) {
      const { min, max } = currentEvent.teamConfig;
      const options = Array.from({ length: max - min + 1 }, (_, i) => (min + i).toString());
      const insertionIndex = baseFields.findIndex(f => f.id === 'paymentScreenshot');

      const memberCountSelector = {
        id: 'dynamicMemberCount',
        label: 'Total Team Members',
        type: 'select',
        options: options,
        value: memberCount.toString(),
        onChange: (e) => setMemberCount(parseInt(e.target.value, 10)),
        required: true,
        fullWidth: true
      };

      const memberFields = [];
      const labelPrefix = selectedEvent === 'gaming' ? 'Player' : 'Member';

      for (let i = 2; i <= memberCount; i++) {
        memberFields.push({
          id: `player${i}Name`,
          label: `${labelPrefix} ${i} Name`,
          placeholder: `Name of ${labelPrefix.toLowerCase()} ${i}`,
          required: true,
          fullWidth: true
        });
      }
      baseFields.splice(insertionIndex, 0, memberCountSelector, ...memberFields);
    }
    return baseFields;
  };

  const handleMasterSubmit = async (formData) => {
    if (isSubmitting) return;
    setIsSubmitting(true);
    // Simulate API logic
    setTimeout(() => {
        setIsSubmitting(false);
        setIsRegistered(true);
        window.scrollTo({ top: 0, behavior: 'smooth' });
    }, 1500);
  };

  return (
    <div className="text-white min-h-screen w-full relative bg-[#030f0a] flex flex-col overflow-x-hidden">
      {/* Background Layer */}
      <div className="fixed inset-0 z-0 pointer-events-none">
        {currentEvent.bgImage && (
          <img
            key={currentEvent.bgImage}
            src={currentEvent.bgImage}
            alt="BG"
            className="w-full h-full object-cover opacity-20 transition-opacity duration-1000"
          />
        )}
        <div className="absolute inset-0 bg-gradient-to-b from-black via-[#030f0a]/80 to-black"></div>
      </div>

      <button
        onClick={() => navigate('/events')}
        className="fixed top-4 right-4 sm:top-6 sm:right-6 z-[250] font-orbitron text-[10px] uppercase tracking-widest text-[#39ff14] border border-[#39ff14]/50 px-4 py-2 rounded-full bg-black/80 backdrop-blur-md hover:bg-[#39ff14] hover:text-black transition-all cursor-pointer"
      >
        ✕ Exit
      </button>

      <SidebarNav activeNav={activeNav} setActiveNav={setActiveNav} />
      <SocialSidebar />
      <Footer activeNav={activeNav} setActiveNav={setActiveNav} />

      {/* Main Content Area */}
      <div className="flex-1 w-full relative px-4 sm:px-8 md:pl-28 md:pr-24 pb-24 z-10">

        {/* Dropdown Selector */}
        <div className="mt-20 sm:mt-24 mb-10 flex flex-col items-center relative w-full max-w-[320px] mx-auto">
          {!isRegistered && (
            <button
              onClick={() => setIsDropdownOpen(!isDropdownOpen)}
              className="group relative w-full bg-black/80 backdrop-blur-2xl border border-[#39ff14]/30 p-4 rounded-md flex items-center justify-between transition-all hover:border-[#39ff14]"
            >
              <div className="flex flex-col items-start text-left">
                <span className="text-[7px] text-[#00ffd5] font-orbitron uppercase tracking-widest opacity-50 mb-1">Select Event</span>
                <span className="text-[#39ff14] font-orbitron text-[11px] uppercase tracking-wider truncate max-w-[200px]">{currentEvent.name}</span>
              </div>
              <svg className={`w-4 h-4 text-[#39ff14] transition-transform ${isDropdownOpen ? 'rotate-180' : ''}`} fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
              </svg>
            </button>
          )}

          {isDropdownOpen && (
            <div className="absolute top-full left-0 w-full mt-2 bg-[#030f0a] border border-[#39ff14]/20 rounded-md overflow-hidden z-[300] shadow-2xl">
              <div className="max-h-[300px] overflow-y-auto py-1 no-scrollbar">
                {Object.keys(EVENTS).map((key) => (
                  <button
                    key={key}
                    onClick={() => handleSelect(key)}
                    className={`w-full text-left px-5 py-3 font-orbitron text-[10px] uppercase tracking-widest transition-all
                      ${selectedEvent === key ? 'bg-[#39ff14]/10 text-[#39ff14]' : 'text-gray-400 hover:bg-white/5 hover:text-white'}`}
                  >
                    {EVENTS[key].name}
                  </button>
                ))}
              </div>
            </div>
          )}
        </div>

        <main className="flex flex-col items-center w-full">
          {!isRegistered ? (
             <div className="w-full max-w-4xl animate-in fade-in slide-in-from-bottom-4 duration-700">
                <div className="text-center mb-10">
                    <h1 className="font-orbitron text-3xl sm:text-5xl md:text-6xl font-bold text-[#39ff14] uppercase tracking-tighter drop-shadow-[0_0_15px_rgba(57,255,20,0.4)]">
                      {currentEvent.name}
                    </h1>
                    <div className="flex justify-center flex-wrap gap-4 mt-6 font-orbitron text-[9px] sm:text-[11px] text-[#00ffd5] opacity-80 uppercase tracking-widest">
                      <span className="bg-[#39ff14]/10 px-3 py-1 rounded border border-[#39ff14]/20">VENUE: {currentEvent.venue}</span>
                      <span className="bg-[#39ff14]/10 px-3 py-1 rounded border border-[#39ff14]/20">DATE: {currentEvent.date}</span>
                    </div>

                    <div className="mt-10 bg-black/60 backdrop-blur-md border border-white/5 p-6 sm:p-10 rounded-2xl text-left shadow-2xl">
                      <p className="text-gray-300 font-light text-sm sm:text-lg leading-relaxed mb-8">{currentEvent.description}</p>

                      <div className={`grid grid-cols-1 ${hasEntryFee ? 'lg:grid-cols-2' : ''} gap-10 border-t border-white/10 pt-10`}>
                        <div className="space-y-4 text-xs sm:text-base uppercase tracking-wider text-[#39ff14]/90 font-orbitron">
                          {currentEvent.details.map((detail, index) => (
                            <p key={index} className="flex items-start gap-2">
                                <span className="text-[#00ffd5]">▶</span> {detail}
                            </p>
                          ))}
                        </div>

                        {hasEntryFee && (
                          <div className="bg-white/5 p-8 rounded-2xl border border-white/10 flex flex-col items-center text-center group transition-all hover:border-[#39ff14]/40">
                              <p className="text-[#39ff14] text-[11px] font-orbitron mb-6 tracking-widest uppercase">Scan to Complete Payment</p>
                              <div className="relative p-3 bg-white rounded-xl mb-6">
                                <img src="/qrcode.png" alt="QR" className="w-48 h-48 sm:w-56 sm:h-56 object-contain" />
                                <div className="absolute inset-0 border-2 border-black/5 rounded-xl"></div>
                              </div>
                              <a href="/qrcode.png" download className="w-full py-3 bg-[#39ff14] text-black font-orbitron font-bold text-xs rounded-lg hover:brightness-110 transition-all uppercase tracking-tighter">Save QR Code</a>
                          </div>
                        )}
                      </div>
                    </div>
                </div>

                {/* The Form Component */}
                <RegistrationForm
                  key={`${selectedEvent}-${memberCount}`}
                  title={`${currentEvent.name} Interface`}
                  fields={getDynamicFields()}
                  onSubmit={handleMasterSubmit}
                  isSubmitting={isSubmitting}
                />
             </div>
          ) : (
            /* Success Interface */
            <div className="max-w-2xl w-full bg-black/90 border-2 border-[#39ff14]/50 p-10 sm:p-16 rounded-3xl text-center shadow-[0_0_50px_rgba(57,255,20,0.2)] animate-in zoom-in duration-500 my-20">
                <div className="w-20 h-20 sm:w-28 sm:h-28 border-2 border-[#39ff14] rounded-full flex items-center justify-center mx-auto mb-10 shadow-[0_0_30px_#39ff14]">
                    <svg className="w-10 h-10 sm:w-14 sm:h-14 text-[#39ff14]" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="3" d="M5 13l4 4L19 7" />
                    </svg>
                </div>
                <h2 className="font-orbitron text-2xl sm:text-4xl text-[#39ff14] tracking-tighter uppercase mb-6 leading-tight">
                    Registration Verified
                </h2>
                <p className="font-roboto text-gray-400 text-sm sm:text-xl leading-relaxed mb-12">
                    Data packets successfully uploaded. You are now part of the <span className="text-[#39ff14] font-bold">{currentEvent.name}</span> protocol. Join the hub below.
                </p>

                <a
                    href={currentEvent.whatsappLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="inline-flex items-center justify-center gap-4 bg-[#25D366] text-white px-10 py-5 rounded-xl font-orbitron text-sm sm:text-lg uppercase tracking-widest hover:brightness-110 transition-all transform hover:scale-105 active:scale-95 shadow-xl"
                >
                    Join WhatsApp Hub
                </a>

                <button
                    onClick={() => setIsRegistered(false)}
                    className="block mx-auto mt-12 font-orbitron text-[10px] text-[#00ffd5]/40 hover:text-[#39ff14] transition-colors uppercase tracking-[0.3em]"
                >
                    ← Back to Interface
                </button>
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default EventMasterTerminal;
